import React from "react"
import {nanoid} from "nanoid"
import Title from "./Components/Title"
import Die from "./Components/Die"


export default function App() {
  const [dice, setDice] = React.useState(diceRoll())

  const [tenzies, setTenzies] = React.useState(false)

  React.useEffect(() => {
    const allHeld = dice.every(die => die.isHeld)
    const firstValue = dice[0].value
    const allSameValue = dice.every(die => die.value === firstValue)
    if (allHeld && allSameValue) {
        setTenzies(true)
        console.log("You won!")
    }
  }, [dice])

  function diceRoll() {
    const diceArr = []
    for (let i = 0; i < 10; i++) {
      diceArr.push({
        value: Math.floor(Math.random() * 6 ) + 1,
        isHeld: false,
        id: nanoid()
      })
    }
    return diceArr
  }

  function roll() {
    const tempAr = dice.map(die => die.isHeld ? die : {...die, value: Math.floor(Math.random() * 6 ) + 1})
    setDice(tempAr)
  }

  function toggleHold(dieId) {
    const tempAr = dice.map(die => die.id === dieId ? {...die, isHeld: !die.isHeld} : die)
    setDice(tempAr)
  }

  const diceBoard = dice.map(die => (
    <Die
      key={die.id}
      value={die.value}
      isHeld = {die.isHeld}
      toggleHold = {() => toggleHold(die.id)}
    />
  ))

  return (
    <main>
      <Title />
      <div className="game-container">
        {diceBoard}
      </div>
      <button onClick={roll}>ROLL</button>
    </main>
  )
}
